import React, { useState } from 'react'
import styled from 'styled-components';

import useInput from '../../hooks/useInput';
import { Form, InputContainer, Input } from '../LoginForm/style';

const VerifyBtn = styled.button`
    width: 9%;
    color:#5DB09B;
    font-size:0.8rem;
`;

const SignupForm = () => {
    const [id, onChangeId] = useInput('');
    const [name ,onChangeName] = useInput('');
    const [password, onChangePassword] = useInput('');
    const [password2, onChangePassword2] =useInput('');
    const [idChecked, setIdChecked] = useState('false');
    const [nameChecked, setNameChecked] = useState('false');
    const [btnActivation, setBtnActivation] = useState(false);

    return (
        <Form style={{height:'400px', marginTop:'30px'}}>
            <InputContainer style={{width:'70%'}}>
                <Input type="text" value={id} placeholder="아이디" autoComplete="off" onChange={onChangeId} />
            </InputContainer>
            <InputContainer style={{width:'70%'}}>
                <Input type="text" value={name} placeholder="닉네임" autoComplete="off" onChange={onChangeName} />
            </InputContainer>
            <InputContainer style={{width:'70%'}}>
                <Input type="text" value={password} placeholder="비밀번호" autoComplete="off" onChange={onChangePassword} />
            </InputContainer>
            <InputContainer style={{width:'70%'}}>
                <Input type="text" value={password2} placeholder="비밀번호 확인" autoComplete="off" onChange={onChangePassword2} />
            </InputContainer>
        </Form>
    );
};

export default SignupForm;
