import React, { useState, useRef, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faChevronLeft, faChevronRight } from '@fortawesome/free-solid-svg-icons';

import useWindowSize from '../../hooks/useWindowSize';
import { Main, BtnContainer, MenuBtn, Slide, PageContainer } from './style';
import StockPage from '../StockPage';

const ManagerPage = () => {
    const winSize = useWindowSize();
    const [curPage, setCurPage] = useState(1);
    const slideRef = useRef(null);

    useEffect(() => {
        slideRef.current.style.transform = `translateX(-${curPage*100/3}%)`;
    }, [curPage]);

    return (
        <Main style={{height:winSize[1]}}>
            <Slide ref={slideRef} style={{width:winSize[0]*3}}>
                <PageContainer style={{width:winSize[0], height:winSize[1]}}>
                    <StockPage />
                </PageContainer>
                <PageContainer style={{width:winSize[0], height:winSize[1]}}>
                    <BtnContainer>
                        <MenuBtn className="btn-hover" onClick={()=>setCurPage(2)}>
                            주문 확인
                        </MenuBtn>
                        <MenuBtn className="btn-hover" onClick={()=>setCurPage(0)}>
                            재고 관리
                        </MenuBtn>
                    </BtnContainer>
                </PageContainer>
                <PageContainer style={{width:winSize[0], height:winSize[1]}}>
                    주문 확인 페이지
                </PageContainer>
            </Slide>
            <button 
                onClick={()=>setCurPage(1)}
                className="btn-hover"
                style={{
                    position:'absolute',
                    top:'50%',
                    right:'30px',
                    display:curPage===0?'':'none',
                }}>
                <FontAwesomeIcon 
                    icon={faChevronRight} 
                    style={{ color:'#4c4a49', fontSize:'2.5rem',}} />
            </button>
            <button 
                onClick={()=>setCurPage(1)}
                className="btn-hover"
                style={{
                    position:'absolute',
                    top:'50%',
                    left:'30px',
                    display:curPage===2?'':'none',
                }}>
                <FontAwesomeIcon 
                    icon={faChevronLeft} 
                    style={{color:'#4c4a49',fontSize:'2.5rem'}} />
            </button>
        </Main>
    );
};

export default ManagerPage;
