import React, { useState ,useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';

import { FormContainer, ContentCard, OptionLi, NameCard } from './style';
import { optionsDummy } from './dummy';

const getOptionList = (dinnerId) => {
    return optionsDummy;
};

const OptionSelectForm = ({ dinnerId }) => {
    const [optionList, setOptionList] = useState({});
    const [extraBtnAct, setExtraBtnAct] = useState([]);

    useEffect(() => {
        const opList = getOptionList(dinnerId);

        setOptionList(opList);
        setExtraBtnAct(Array(opList.options.length).fill(0));
    }, []);

    return (
        <FormContainer>
            <ContentCard style={{
                flexWrap:'wrap', 
                padding:'50px 0 20px',
                justifyContent:'center',
                }}>
                {optionList.options.map((option, i) => {
                    return (
                        <OptionLi>
                            <NameCard>
                                {option.menuName}
                            </NameCard>
                        </OptionLi>
                    );
                })}
            </ContentCard>
        </FormContainer>
    );
};

export default OptionSelectForm
