import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';

import theme from '../style/theme';

const HeaderContainer = styled.header`
    width:100%;
    height:120px;
    padding: 0 20px;

    position:fixed;
    top:0;
    left:0;
    right:0;
    z-index:1;
`;

const Logo = styled.h1`
    width:60%;
    height:100%;
    margin:0 auto;
    color:#ddd;
    font-family: 'SDKukdetopokki-Lt', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size: 2.5rem;

    display:flex;
    align-items:center;

    @media ${theme.tabletL} {
        width:80%;
    }

    @media ${theme.mobileL} {
        width:90%;
    }
`;

const Header = ({ headerType }) => {
    return (
        <HeaderContainer style={{
            borderBottom: headerType ? '1px solid #dfdfdf' : '',
            backgroundColor: headerType ? '#fff' : '',
            }}>
                <Logo>
                    미스터대박
                </Logo>
        </HeaderContainer>
    );
};

Header.propTypes = {
    headerType: PropTypes.number.isRequired,
};

export default Header;
