import React, { useState, useEffect } from 'react';
import axios from 'axios';

import useWindowSize from '../../hooks/useWindowSize';
import { Main, Content, Title, Th, Td } from './style';
import LoadingPage from '../LoadingPage';

const StockPage = () => {
    const winSize = useWindowSize();
    const [loaded, setLoaded] = useState(false);
    const [data, setData] = useState(null);

    useEffect(() => {
        const getStockInfo = async () => {
            await axios.get('/stock/read')
            .then(res => {
                setData(res.data);
                setLoaded(true);
            })
            .catch(err => {
                console.error(err);
            });
        };

        getStockInfo();
    }, []);

    return (
        <>
        {!loaded ?
        <LoadingPage /> :
        <Main style={{minHeight:winSize[1], overflow:'auto'}}>
            <Content>
                <div style={{diplay:'flex', width:'100%', marginBottom:'30px'}}>
                    <Title>
                        재고 정보
                    </Title>
                </div>
                <table style={{width:'100%'}}>
                    <thead>
                        <tr style={{display:'flex'}}>
                            <Th style={{width:'30px'}}>No</Th>
                            <Th style={{width:'calc((100%-110px)*2/5)'}}>메뉴 이름</Th>
                            <Th style={{width:'80px'}}>수량</Th>
                            <Th style={{width:'calc((100%-110px)*3/5)'}}>비고</Th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((v, i) => {
                            return(
                            <tr style={{diplay:'flex'}}>
                                <Th style={{width:'30px'}}>{i+1}</Th>
                                <Th style={{width:'calc((100%-110px)*2/5)'}}>d</Th>
                                <Th style={{width:'80px'}}>d</Th>
                                <Th style={{width:'calc((100%-110px)*3/5)'}}>d</Th>
                            </tr>
                            );
                        })}
                    </tbody>
                </table>
            </Content>
        </Main>}
        </>
    );
};

export default StockPage;
