import React, { useState ,useEffect, useCallback } from 'react';
import { useDispatch } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusCircle, faMinusCircle } from '@fortawesome/free-solid-svg-icons';

import { FormContainer, ContentCard, OptionLi, NameCard, Title, SizeUpBtn } from './style';
import { optionsDummy } from './dummy';
import LoadingPage from '../LoadingPage';

const getOpList = () => {
    return optionsDummy;
};

const OptionSelectForm = ({ dinnerId }) => {
    const [optionList, setOptionList] = useState({});
    const [extraBtnAct, setExtraBtnAct] = useState([]);
    const [loaded, setLoaded] = useState(false);

    const sizeup = useCallback((idx) => {
        setExtraBtnAct(prev => {
            const newState = [...prev];
            newState[idx] ^= 1;

            return newState;
        });
    }, []);

    const removeOption = useCallback((idx) => {

    }, []);

    const onClickOptionCard = useCallback((e) => {
        const sizeupBtnIdx = e.target.dataset.sizeupBtn;
        const minusIconIdx = e.target.dataset.minusIcon;

        if(sizeupBtnIdx !== undefined) {
            sizeup(sizeupBtnIdx);
        } else if(minusIconIdx!== undefined) {
            removeOption(minusIconIdx);
        }
    }, []);

    useEffect(() => {
        const opList = getOpList();

        setOptionList(opList);
        setExtraBtnAct(Array(opList.options.length).fill(0));
        setLoaded(true);
    }, []);

    return (
        <>
        {loaded ?
        <FormContainer>
            <ContentCard 
                onClick={onClickOptionCard}
                style={{
                    flexWrap:'wrap', 
                    padding:'30px 0 20px',
                    justifyContent:'center',
                    }}>
                <Title style={{
                    fontSize:'1.4rem',
                    width:'90%',
                }}>
                    옵션
                </Title>
                {optionList.options.map((option, i) => {
                    return (
                        <OptionLi>
                            <div style={{display:'flex'}}>
                                <NameCard style={{width:'80px'}}>
                                    {option.menuName}
                                </NameCard>
                                <div style={{
                                    fontSize:"0.9rem",
                                    color:"#9c9a99"
                                }}>
                                    {option.measure === '' 
                                    ? (extraBtnAct[i] ? 'extra' : 'normal')
                                    : Math.ceil(parseInt(option.size) * (extraBtnAct[i] ? 1.5 : 1)) + option.measure
                                    }
                                </div>
                            </div>
                            <div style={{display:'flex'}}>
                                <SizeUpBtn 
                                    style={{
                                        backgroundColor: extraBtnAct[i] ? '#3D7365' : '',
                                        color: extraBtnAct[i] ? '#eee' : '',
                                    }}
                                    className="btn-hover" 
                                    data-sizeup-btn={i}
                                    >
                                        size up
                                    </SizeUpBtn>
                                <FontAwesomeIcon 
                                    data-minus-icon={i}
                                    className="btn-hover"
                                    icon={faMinusCircle} 
                                    style={{
                                        color:'red',
                                        fontSize:'0.9rem',
                                        position:'relative',
                                        top:'4px',
                                    }}
                                    />
                            </div>
                        </OptionLi>
                    );
                })}
            </ContentCard>
            <ContentCard style={{borderStyle:'dashed'}}>
                <Title style={{
                        fontSize:'1.4rem',
                        width:'90%',
                    }}>
                    추가하기
                </Title>
                {optionList.extra.map((list, i) => {
                    return (
                        <OptionLi>
                            <NameCard>
                                {list.menuName}
                            </NameCard>
                            <FontAwesomeIcon 
                                    data-plus-icon={i}
                                    className="btn-hover"
                                    icon={faPlusCircle} 
                                    style={{
                                        color:'green',
                                        fontSize:'0.9rem',
                                        position:'relative',
                                        top:'4px',
                                    }}
                                    />
                        </OptionLi>
                    );
                })}
            </ContentCard>
        </FormContainer> :
        <LoadingPage /> }
        </>
    );
};

export default OptionSelectForm
