import React, { useState ,useEffect, useCallback } from 'react';
import { useDispatch } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusCircle, faMinusCircle } from '@fortawesome/free-solid-svg-icons';

import { FormContainer, ContentCard, OptionLi, NameCard, Title, SizeUpBtn } from './style';
import { optionsDummy } from './dummy';
import LoadingPage from '../LoadingPage';

const getOpList = () => {
    return optionsDummy;
};

const OptionSelectForm = ({ dinnerId }) => {
    const [optionList, setOptionList] = useState({});
    const [extraBtnAct, setExtraBtnAct] = useState([]);
    const [loaded, setLoaded] = useState(false);

    const sizeup = useCallback((idx) => {
        setExtraBtnAct(prev => {
            const newState = [...prev];
            newState[idx] ^= 1;

            return newState;
        });
    }, []);

    const removeOption = (idx) => {
        const opList = [];
        optionList.options.forEach(v => {
            const tmp = {...v};
            opList.push(tmp);
        });

        const extList = [];
        optionList.extra.forEach(v => {
            const tmp = {...v};
            extList.push(tmp);
        });
        extList.push(opList[idx]);

        for(let i = idx+1; i < opList.length; i++) {
            opList[i-1] = opList[i];
        }
        opList.pop();

        setOptionList(prev => {
            return {
                options: opList,
                extra: extList,
            }
        });
    };

    const onClickOptionCard = useCallback((e) => {
        const sizeupBtnIdx = e.target.dataset.sizeupBtn;
        const minusIconIdx = e.target.dataset.minusIcon;
        console.log(minusIconIdx);
        if(sizeupBtnIdx !== undefined) {
            sizeup(parseInt(sizeupBtnIdx));
        } else if(minusIconIdx!== undefined) {
            removeOption(parseInt(minusIconIdx));
        }
    }, [optionList]);

    useEffect(async () => {
        const opList = getOpList();

        setOptionList(opList);
        setExtraBtnAct(Array(opList.options.length).fill(0));
        setLoaded(true);
    }, []);

    return (
        <>
        {loaded ?
        <FormContainer>
            <ContentCard 
                onClick={onClickOptionCard}
                style={{
                    flexWrap:'wrap', 
                    padding:'30px 0 20px',
                    justifyContent:'center',
                    }}>
                <Title style={{
                    fontSize:'1.4rem',
                    width:'90%',
                }}>
                    옵션
                </Title>
                {optionList.options.map((option, i) => {
                    return (
                        <OptionLi key={option.menuName + i}>
                            <div style={{display:'flex'}}>
                                <NameCard style={{width:'80px'}}>
                                    {option.menuName}
                                </NameCard>
                                <div style={{
                                    fontSize:"0.9rem",
                                    color:"#9c9a99"
                                }}>
                                    {option.measure === '' 
                                    ? (extraBtnAct[i] ? 'extra' : 'normal')
                                    : Math.ceil(parseInt(option.size) * (extraBtnAct[i] ? 1.5 : 1)) + option.measure
                                    }
                                </div>
                            </div>
                            <div style={{display:'flex'}}>
                                <SizeUpBtn 
                                    style={{
                                        backgroundColor: extraBtnAct[i] ? '#3D7365' : '',
                                        color: extraBtnAct[i] ? '#eee' : '',
                                    }}
                                    className="btn-hover" 
                                    data-sizeup-btn={i}
                                    >
                                        size up
                                    </SizeUpBtn>
                                <button className="btn-hover" data-minus-icon={i}>
                                    <FontAwesomeIcon 
                                        data-minus-icon={i}
                                        className="btn-hover"
                                        icon={faMinusCircle} 
                                        style={{
                                            color:'red',
                                            fontSize:'0.9rem',
                                            position:'relative',
                                            top:'4px',
                                        }}
                                        />
                                </button>
                            </div>
                        </OptionLi>
                    );
                })}
            </ContentCard>
            <ContentCard style={{
                borderStyle:'dashed',
                flexWrap:'wrap', 
                padding:'30px 0 20px',
                justifyContent:'center',
                }}>
                <Title style={{
                        fontSize:'1.4rem',
                        width:'90%',
                    }}>
                    추가하기
                </Title>
                {optionList.extra.map((list, i) => {
                    return (
                        <OptionLi key={list.menuName+i}>
                            <NameCard>
                                {list.menuName}
                            </NameCard>
                            <FontAwesomeIcon 
                                    data-plus-icon={i}
                                    className="btn-hover"
                                    icon={faPlusCircle} 
                                    style={{
                                        color:'green',
                                        fontSize:'0.9rem',
                                        position:'relative',
                                        top:'4px',
                                    }}
                                    />
                        </OptionLi>
                    );
                })}
            </ContentCard>
        </FormContainer> :
        <LoadingPage /> }
        </>
    );
};

export default OptionSelectForm
