import styled from 'styled-components';
import theme from '../../style/theme';

export const Container = styled.div`
    width:80%;
    min-height:400px;
`;

export const Title = styled.div`
    font-family: 'GmarketSansMedium', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size:1.6rem;
    font-weight:bold
    color:#333;
    border-bottom: 1px solid #bfbfbf;
    padding-bottom: 7px;
    margin-bottom: 7px;
`;

export const FormContainer = styled.div`
    width:100%;
    display:flex;
    margin-top:50px;
    flex-direction:column;
    flex-wrap:wrap;
    align-items:center;
`;

// #3D7365 
// rgba(61, 115, 101, 0.7)
export const ContentCard = styled.div`
    width:80%;
    height:80px;
    margin: 20px 0;
    padding-left:20px;
    overflow-x:none;
    border: 1px solid #3D7365;
    border-radius:10px;

    display:flex;
    justify-content:space-between;
    align-items:center;

    @media ${theme.mobileL} {
        width:100%;
    }
`;

export const NameCard = styled.div`
    color:#4c4a49;
    font-size:1.5rem;
    font-weight:bold;
    padding: 3px 0;

    @media ${theme.mobileL} {
        font-size:1.3rem;
    }

    @media ${theme.mobileS} {
        font-size:1rem;
    }
`;

export const SelectBtn = styled.button`
    width:100px;
    height:80px;
    border-radius: 0 10px 10px 0;
    background-color: #3D7365;
    color:#eee;

    @media ${theme.mobileL} {
        width:80px;
    }
`;

export const ToggleBtn = styled.button`
    color:#8c8a89;
    font-size:0.7rem;
    position:relative;
    top:2px;
`;

export const Drop = styled.div`
    width: 150px;
    box-shadow: 1px 1px 1px 1px #bfbfbf;
    padding:15px 10px;
    border-radius:3px;
    background-color:#f3f1f0;

    font-size:0.8rem;
    color:#6c6a69;
    line-height:1.3;

    position:absolute;
    top:25px;
    left:-20px;
`;
