import React, { useState ,useEffect, useCallback } from 'react';
import { useDispatch } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPlusCircle, faMinusCircle } from '@fortawesome/free-solid-svg-icons';

import { FormContainer, ContentCard, OptionLi, NameCard, Title, SizeUpBtn } from './style';
import { optionsDummy } from './dummy';
import LoadingPage from '../LoadingPage';

const getOpList = () => {
    console.log('test');
    return optionsDummy;
};

const OptionSelectForm = ({ dinnerId }) => {
    const [optionList, setOptionList] = useState({});
    const [extraBtnAct, setExtraBtnAct] = useState([]);
    const [loaded, setLoaded] = useState(false);


    useEffect(() => {
        const opList = getOpList();

        setOptionList(opList);
        setExtraBtnAct(Array(opList.options.length).fill(0));
        setLoaded(true);
    }, []);
    console.log(optionList);
    return (
        <>
        {loaded ?
        <FormContainer>
            <ContentCard style={{
                flexWrap:'wrap', 
                padding:'30px 0 20px',
                justifyContent:'center',
                }}>
                <Title style={{
                    fontSize:'1.4rem',
                    width:'90%',
                }}>
                    옵션
                </Title>
                {optionList.options.map((option, i) => {
                    return (
                        <OptionLi>
                            <div style={{display:'flex'}}>
                                <NameCard style={{width:'80px'}}>
                                    {option.menuName}
                                </NameCard>
                                <div style={{
                                    fontSize:"0.9rem",
                                    color:"#9c9a99"
                                }}>
                                    {option.measure === '' 
                                    ? (option.size === 1 ? 'normal' : 'extra')
                                    : `${option.size} ${option.measure}`
                                    }
                                </div>
                            </div>
                            <div style={{display:'flex'}}>
                                <SizeUpBtn className="btn-hover">size up</SizeUpBtn>
                                <FontAwesomeIcon 
                                    icon={faMinusCircle} 
                                    style={{
                                        color:'red',
                                        fontSize:'0.9rem',
                                        position:'relative',
                                        top:'5px',
                                    }}
                                    />
                            </div>
                        </OptionLi>
                    );
                })}
            </ContentCard>
        </FormContainer> :
        <LoadingPage /> }
        </>
    );
};

export default OptionSelectForm
