import React, { useCallback, useState, useRef, useEffect } from 'react'
import axios from 'axios';
import styled from 'styled-components';

import useInput from '../hooks/useInput';
import theme from '../style/theme';
import { Form, InputContainer, Input } from './LoginForm/style';

const url = 'https://mr-daebak.herokuapp.com'
// const url = 'http://localhost:5000'

const VerifyBtn = styled.button`
    width: 19%;
    color:#5DB09B;
    font-size:0.7rem;
    border:1px solid black;

    @media ${theme.mobileL} {
        width:22%
    }

    @media ${theme.mobileS} {
        width:30%
    }
`;

const InputContainer2 = styled(InputContainer)`
    width:70%;
    display:flex;
    justify-content:between

    @media ${theme.mobileL} {
        width:85%
    }

    @media ${theme.mobileS} {
        width:95%

`;

const Input2 = styled(Input)`
    width:80%;

    @media ${theme.mobileL} {
        width:70%
    }    

    @media ${theme.mobileS} {
        width:60%
    }    
`;

const Indicator = styled.div`
    width: 70%;
    height:15px;
    padding: 0 4px;
    color:#5DB09B;
    font-size:0.7rem;

    display:flex;
    align-items:center;
`;

const SignupForm = () => {
    const [id, onChangeId] = useInput('');
    const [name ,onChangeName] = useInput('');
    const [password, onChangePassword] = useInput('');
    const [password2, onChangePassword2] =useInput('');
    const [idFocus, setIdFocus] = useState(false);
    const [nameFocus, setNameFocus] = useState(false);
    const [pwFocus, setPwFocus] = useState(false);
    const [Pw2Focus, setPw2Focus] = useState(false);
    const [idChecked, setIdChecked] = useState(false);
    const [nameChecked, setNameChecked] = useState(false);
    const [validPw, setValidPw] = useState(false);
    const [btnActivation, setBtnActivation] = useState(false);
    const inputRef_id = useRef(null);
    const inputRef_name = useRef(null);
    const inputContRef_id = useRef(null);
    const inputContRef_name = useRef(null);

    const verifyId = useCallback(async () => {
        if(idChecked) return;

        await axios.post(url+'/duplicate_id', {
                user_id : id
            }, {
            headers: {
                'Content-Type': 'application/json',
            }
        })
        .then(res => {
            if(res.data.check_id_dup && confirm('사용가능한 아이디입니다. 사용하시겠습니까?')) {
                setIdChecked(true);
                inputRef_id.current.setAttribute('readonly', 'readonly');
                inputRef_id.current.style.backgroundColor = '#dfdfdf'
                inputContRef_id.current.style.backgroundColor = '#dfdfdf'
            } else {
                alert('이미 사용 중인 아이디입니다.');
            }
        })
        .catch(err => console.error(err));
    }, [id, idChecked]);

    const verifyName = useCallback(async () => {
        if(nameChecked) return;

        await axios.post(url+'/duplicate_name', {
            user_name : name
        }, {
        headers: {
            'Content-Type': 'application/json',
        }
    })
    .then(res => {
        if(res.data.check_name_dup && confirm('사용가능한 닉네임입니다. 사용하시겠습니까?')) {
            setNameChecked(true);
            inputRef_name.current.setAttribute('readonly', 'readonly');
            inputRef_name.current.style.backgroundColor = '#dfdfdf'
            inputContRef_name.current.style.backgroundColor = '#dfdfdf'
        } else {
            alert('이미 사용 중인 닉네임입니다.');
        }
    })
    .catch(err => console.error(err));
    }, [name, nameChecked]);

    const isValidPw = useCallback((e) => {
        onChangePassword(e);
    }, []);

    return (
        <Form style={{height:'400px', marginTop:'30px'}} onSubmit={e => { e.preventDefault(); }}>
            {/* id input */}
            <InputContainer2 ref={inputContRef_id}>
                <Input2 ref={inputRef_id} type="text" value={id} placeholder="아이디" autoComplete="off"
                    onChange={onChangeId} 
                    onFocus={()=>setIdFocus(true)}
                    onBlur={()=>setIdFocus(false)}
                    />
                <VerifyBtn className="btn-hover" onClick={verifyId}>중복확인</VerifyBtn>
            </InputContainer2>
            <Indicator style={{color:idChecked?'':'#B36A59'}}>
                {idFocus && (idChecked ? '사용 가능한 아이디입니다.' : '아이디 중복확인을 해주세요')}
            </Indicator>
            {/* name input */}
            <InputContainer2 ref={inputContRef_name}>
                <Input2 ref={inputRef_name} type="text" value={name} placeholder="닉네임" autoComplete="off"
                    onChange={onChangeName} 
                    onFocus={()=>setNameFocus(true)}
                    onBlur={()=>setNameFocus(false)}
                    />
                <VerifyBtn className="btn-hover" onClick={verifyName}>중복확인</VerifyBtn>
            </InputContainer2>
            <Indicator style={{color:idChecked?'':'#B36A59'}}>
                {nameFocus && (nameChecked ? '사용 가능한 닉네임입니다.' : '닉네임 중복확인을 해주세요')}
            </Indicator>
            {/* pw input */}
            <InputContainer2>
                <Input2 type="password" value={password} placeholder="비밀번호" autoComplete="off" onChange={isValidPw} />
            </InputContainer2>
            <InputContainer2>
                <Input2 type="password" value={password2} placeholder="비밀번호 확인" autoComplete="off" onChange={onChangePassword2} />
            </InputContainer2>
        </Form>
    );
};

export default SignupForm;
