import React, { useEffect, useState } from 'react';

import LoadingPage from '../LoadingPage';
import OrderResultCard from '../OrderResultCard';
import { Container, NameCard, FormContainer } from '../OrderForm/style';
import Axios from 'axios';

const OrderList = () => {
    const [loaded, setLoaded] = useState(false);
    const [orderInfo, setOrderInfo] = useState(null);
    const times = ['17:00', '18:00', '19:00', '20:00', '21:00']

    useEffect(async () => {
        await Axios.get('/reservation')
        .then(res => {
            console.log(res.data);
            setOrderInfo(res.data);
        })
        .then(() => {
            setLoaded(true)
        })
        .catch(err => console.error(err));
    }, []);

    return(
        <>
        {!loaded ? <LoadingPage /> :
        <Container>
            {times.map(t => {
                return(
                    <FormContainer>
                        <NameCard>
                            {t}
                        </NameCard>
                        {orderInfo[t].map(info => {
                            <OrderResultCard orderInfo={info} />
                        })}
                    </FormContainer>
                );
            })}
        </Container>}
        </>
    );
};

export default OrderList;
