import React from 'react';
import { useSelector } from 'react-redux';

import { Container, Title, Sub } from './style';
import DinnerSelectForm from './DinnerSelectForm';
import StyleSelectForm from './StyleSelectForm';

const switchSubTitle = (step) => {
    switch(step) {
        case 1:
            return 'Dinner';
        case 2:
            return 'Style';
        case 3:
            return 'Options';
        case 4:
            return 'Order Informatoin';
        default:
            return '';
    }
};

const OrderForm = () => {
    const { step } = useSelector(state => state.order);

    return (
        <Container>
            <Title>
                주문하기
                <Sub>{switchSubTitle(step)}</Sub>
            </Title>
            {step === 1 
            ? <DinnerSelectForm />
            : (step === 2
                ? <StyleSelectForm />
                : <div></div>)}

        </Container>
    );
};

export default OrderForm;
