import React, { useState, useEffect } from 'react';
import { Form } from '../LoginForm/style';

import { FormContainer, NameCard, SubmitBtn,
        TimeBtnContainer, TimeBtn } from './style';
import LoadingPage from '../LoadingPage';
import Axios from 'axios';

const TimeSelectForm = () => {
    const [loaded, setLoaded] = useState(false);
    const [orderedNum, setOrderedNum] = useState([]);
    const times = ['17:00', '18:00', '19:00', '20:00', '21:00'];

    useEffect(async () => {
        await Axios.get('/orderstate?req=orderedNum')
        .then(res => {
            const nums = [];

            times.forEach(t => {
                nums.push(res.data[t]);
            });

            setOrderedNum(nums);
        })
        .then(() => setLoaded(true))
        .catch(err => console.error(err))
    }, []);

    return (
        <>
        {!loaded ? <LoadingPage /> :
        <FormContainer style={{width:'100%'}}>
            <div style={{
                    width:'100%',
                    position:'relative',
                    top: '-40px'
                }}>
                    * 한 타임에 다섯 팀까지만 받습니다.
                    <br />
                    <br />
                    * 배달 시간으로부터 한 시간 전까지만 주문이 가능합니다.
                </div>
                <TimeBtnContainer>
                    {orderedNum.forEach((num, i) => {
                        <TimeBtn data-index={i}>
                            <NameCard>
                                {time[i]}
                            </NameCard>
                            <div>
                                {`${num} / 5`}
                            </div>
                        </TimeBtn>
                    })}
                </TimeBtnContainer>
        </FormContainer>}
        </>
    )
};

export default TimeSelectForm;
