import styled from 'styled-components';
import theme from '../../style/theme';

export const Container = styled.div`
    width:76%;
    min-height:400px;
    margin-top:40px;

    display: flex;
    flex-direction:column;
    align-items:center;
`;

export const Title = styled.div`
    font-family: 'GmarketSansMedium', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size:2rem;
    font-weight:bold
    color:#333;

    width:100%;
    border-bottom: 1px solid #bfbfbf;
    padding-bottom: 10px;
    margin-bottom: 10px;
`;

export const Sub = styled.span`
    font-family: 'GmarketSansMedium', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size:1.3rem;
    color:#aaa;
    display:inline-block;
    margin-left:15px;
`;

export const FormContainer = styled.div`
    width:80%;
    display:flex;
    margin-top:50px;
    flex-direction:column;
    flex-wrap:wrap;
    align-items:center;

    @media ${theme.mobileL} {
        width:100%;
    }
`;

// #3D7365 
// rgba(61, 115, 101, 0.7)
export const ContentCard = styled.div`
    width:100%;
    height:80px;
    margin: 20px 0;
    padding-left:20px;
    overflow-x:none;
    border: 1px solid #3D7365;
    border-radius:10px;

    display:flex;
    justify-content:space-between;
    align-items:center;
`;

export const NameCard = styled.div`
    color:#4c4a49;
    font-size:1.5rem;
    font-weight:bold;
    padding: 3px 0;

    @media ${theme.mobileL} {
        font-size:1.3rem;
    }

    @media ${theme.mobileS} {
        font-size:1rem;
    }
`;

export const SelectBtn = styled.button`
    width:100px;
    height:80px;
    border-radius: 0 10px 10px 0;
    background-color: #3D7365;
    color:#eee;
    font-size:0.8rem;
    line-height:1.5;

    @media ${theme.mobileL} {
        width:80px;
    }
`;

export const ToggleBtn = styled.button`
    color:#8c8a89;
    font-size:0.7rem;
    position:relative;
    top:2px;
`;

export const Drop = styled.div`
    width: 150px;
    box-shadow: 1px 1px 1px 1px #bfbfbf;
    padding:15px 10px;
    border-radius:3px;
    background-color:#f3f1f0;

    font-size:0.8rem;
    color:#6c6a69;
    line-height:1.3;

    position:absolute;
    top:25px;
    left:-20px;
`;

export const SubmitBtn = styled.button`
    width:100px;
    height:40px;
    margin-top:30px;
    border:2px solid #3D7365;
    border-radius:10px;
    font-size:0.9rem;
    color:#3D7365;

    align-self:flex-end;
    display:flex;
    justify-content:center;
    align-items:center;

    &:hover {
        color:#fff;
        background-color:#3D7365;
    }
`;

export const Desc = styled.div`
    color:#8c8a89;
    font-size:0.9rem;
    position:relative;
    top:10px;
    left:15px;

    &:hover div {
        display:black;
    }
`;
