import React, { useState ,useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';

import { FormContainer, ContentCard, OptionLi, NameCard, Title } from './style';
import { optionsDummy } from './dummy';

const getOptionList = () => {
    return optionsDummy;
};

const OptionSelectForm = ({ dinnerId }) => {
    const [optionList, setOptionList] = useState({});
    const [extraBtnAct, setExtraBtnAct] = useState([]);

    useEffect(() => {
        const opList = getOptionList();

        setOptionList(opList);
        setExtraBtnAct(Array(opList.options.length).fill(0));
    }, []);
    console.log(optionList);
    return (
        <FormContainer>
            <ContentCard style={{
                flexWrap:'wrap', 
                padding:'30px 0 20px',
                justifyContent:'center',
                }}>
                <Title style={{
                    fontSize:'1.4rem',
                    width:'90%',
                }}>
                    옵션
                </Title>
                {optionList.options.map((option, i) => {
                    return (
                        <OptionLi>
                            <div style={{display:'flex'}}>
                                <NameCard style={{width:'80px'}}>
                                    {option.menuName}
                                </NameCard>
                                <div style={{
                                    fontSize:"0.9rem",
                                    color:"#9c9a99"
                                }}>
                                    {option.measure === '' 
                                    ? (option.size === 1 ? 'normal' : 'extra')
                                    : option.size + option.measure
                                    }
                                </div>
                            </div>
                            <div style={{display:'flex'}}>
                                dfdf
                            </div>
                        </OptionLi>
                    );
                })}
            </ContentCard>
        </FormContainer>
    );
};

export default OptionSelectForm
