import styled from 'styled-components';

export const Main = styled.main`
    width:100%;
    background-color:#eceae9;
    position:relative;
    overflow:hidden;
    padding: 80px 20px;

    display:flex;
    justify-content:center;
    align-items:center;
`;

export const SmallBtn = styled.button`
    width:40px;
    height:17px;
    border:2px solid #3D7365;
    border-radius:7px;
    font-size:0.7rem;
    color:#3D7365;
    margin-left:10px;

    display:flex;
    justify-content:center;
    align-items:center;

    &:hover {
        color:#fff;
        background-color:#3D7365;
    }
`;
