import React, { useState } from 'react';
import { useDispatch } from 'react-redux';

import { FormContainer, ContentCard, NameCard, SelectBtn, SubmitBtn } from './style';

const StyleSelectForm = () => {
    const [selectedIdx, setSelectedIdx] = useState(-1);

    return (
        <FormContainer>
            <ContentCard style={{boxShadow:selectedIdx==='1'?'0 0 4px 3px #9Dd3c5':''}}>
                <div style={{display:'flex'}}>
                    <NameCard style={{borderBottom:selectedIdx==='1'?'1px dashed #8c8a89':''}}>
                        Simple
                    </NameCard>
                </div>
            </ContentCard>
        </FormContainer>
    );
};  

export default StyleSelectForm;
