import styled from 'styled-components';
import theme from '../../style/theme';

export const Title = styled.h1`
    width:60%;
    margin-bottom:20px;
    font-family: 'GmarketSansMedium', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size:1.2rem;
    font-weight:bold
    color:#333;

    display:flex;
    justify-content:space-between;
`;

export const Form = styled.form`
    width:70%;
    height:300px;
    max-width:500px;
    background-color: #fff;
    border: 1px solid #dfdfdf;
    border-radius:5px;
    box-shadow:0 0 2px 2px #dfdfdf;

    display:flex;
    flex-direction: column;
    align-items: center;
    justify-content:center;
`;

export const InputContainer = styled.div`
    width: 60%;
    height: 35px;
    border: 1px solid ${theme.borderColor};
    border-radius:3px;
    background-color: ${theme.backColorLight};
    margin:3px;
`;

export const Input = styled.input`
    width:90%;
    height:100%;
    background-color: ${theme.backColorLight};
    margin-left:3px;
    font-size: 0.7rem;
`

export const ButtonContainer = styled.div`
    width:60%;
    height:28px;
    margin: 30px 0 20px;

    display:flex;
    justify-content: space-between;
`;

export const Button = styled.button`
    width: 47%;
    height: 100%;
    border-radius:3px;
    background-color:#4c4a49;
    color:#fff;
`;

export const Partition = styled.div`
    width:60%;
    margin-bottom: 30px;

    display:flex;
    justify-content:space-between;
    align-items:center;
`;

export const HorizontalLine = styled.div`
    width:37%;
    height:1px;
    background-color:${theme.borderColor};
`;

export const GuestLogin = styled.button`
    font-size:0.8rem;
    color:#5DB09B;
`;

export const BackBtn = styled.button`
    width:60px;
    height:20px;
    border:2px solid #3D7365;
    border-radius:10px;
    font-size:0.4rem;
    color:#3D7365;

    display:flex;
    justify-content:center;
    align-items:center;

    &:hover {
        color:#fff;
        background-color:#3D7365;
    }
`;
