import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';

import theme from '../style/theme';

const HeaderContainer = styled.header`
    width:100%;
    height:120px;
    padding: 0 20px;

    position:fixed;
    top:0;
    left:0;
    right:0;
    z-index:1;

    transition: all;
    transition-delay: ${props => {
        if(!props.headerType) {
            return '0s';
        } else {
            return '0.5s';
        }
    }};

    border-bottome: ${props => {
        if(props.headerType)
            return '1px solid #dfdfdf';
    }};

    backgrondColor: ${props => {
        if(props.headerType)
            return '#fff';
    }};
`;

const Logo = styled.h1`
    width:60%;
    height:100%;
    margin:0 auto;
    color:#eee;
    font-family: 'GmarketSansMedium', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size: 2.5rem;

    display:flex;
    align-items:center;

    transition: all 0.5s ease-in-out;

    @media ${theme.tabletL} {
        width:80%;
    }

    @media ${theme.mobileL} {
        width:90%;
    }
`;

const Header = ({ headerType }) => {
    return (
        <HeaderContainer style={{
            borderBottom: headerType ? '1px solid #dfdfdf' : '',
            backgroundColor: headerType ? '#fff' : '',
            }}>
                <Logo>
                    미스터<span style={{fontSize:'3.5rem', position:'relative', top:'-2px'}}>대</span>박
                </Logo>
        </HeaderContainer>
    );
};

Header.propTypes = {
    headerType: PropTypes.number.isRequired,
};

export default Header;
