import React, { useState, useEffect } from 'react';

import { dinnerStateDummy } from './dummy';
import { FormContainer, ContentCard, NameCard, SelectBtn } from './style';

const getDinnerState = () => {
    return dinnerStateDummy;
};

const DinnerSelectForm = () => {
    const [dinnerList, setDinnerList] = useState([]);
    const [selectedIdx, setSelectedIdx] = useState(0);

    useEffect(() => {
        setDinnerList(getDinnerState());
    }, []);

    return (
        <FormContainer>
            {dinnerList.map((content, i) => {
                return (
                <ContentCard 
                    key={content.dinnerName + i}
                    style={{boxShadow:selectedIdx===i?'0 0 2px 2px #9Dd3c5':''}}>
                    <NameCard 
                        style={{borderBottom:selectedIdx===i?'1px dashed #8c8a89':''}}>
                        {content.dinnerName}
                    </NameCard>
                    <SelectBtn className="btn-hover">
                        선택
                    </SelectBtn>
                </ContentCard>
                );
            })}
        </FormContainer>
    );
}

export default DinnerSelectForm;
