import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Link from 'next/link';

import theme from '../style/theme';
import useInput from '../hooks/useInput';

const Form = styled.form`
    width:70%;
    height:400px;
    max-width:400px;
    background-color: #fff;

    display:flex;
    flex-direction: column;
    align-items: center;
`;


const Title = styled.div`
    width:180px;
    margin:25px 0 25px;
    font-family: 'SDKukdetopokki-Lt', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size:1.2rem;

    display:flex;
    justify-content:center;
`;

const InputContainer = styled.div`
    width: 60%;
    height: 35px;
    border: 1px solid ${theme.borderColor};
    border-radius:3px;
    background-color: ${theme.backColorLight};
    margin:3px;
`;

const Input = styled.input`
    width:90%;
    height:100%;
    background-color: ${theme.backColorLight};
    margin-left:3px;
    font-size: 0.7rem;

    &:focus .input-container {
        border: 1px solid red;
    }
`

const ButtonContainer = styled.div`
    width:60%;
    height:28px;
    margin: 30px 0 20px;

    display:flex;
    justify-content: space-between;
`;

const Button = styled.button`
    width: 47%;
    height: 100%;
    border-radius:3px;
    color:#fff;
`;

const Partition = styled.div`
    width:60%;
    margin-bottom: 30px;

    display:flex;
    justify-content:space-between;
    align-items:center;
`;

const HorizontalLine = styled.div`
    width:37%;
    height:1px;
    background-color:${theme.borderColor};
`;

const GuestLogin = styled.button`
    font-size:0.8rem;
    color:${theme.fontSkyBlue};
`;

const LoginForm = () => {
    const [id, onChangeId] = useInput('');
    const [password, onChangePassword] = useInput('');
    const [btnActivation, setBtnActivation] = useState(false);

    useEffect(() => {
        if(id != '' && password.length > 5) {
            setBtnActivation(true);
        } else {
            setBtnActivation(false);
        }
    }, [id, password]);

    return (
        <Form onSubmit={e => { e.preventDefault(); }}>
            <Title>미스터대박</Title>
            <InputContainer className="input-container">
                <Input type="text" value={id} placeholder="아이디" autoComplete="off" onChange={onChangeId} />
            </InputContainer>
            <InputContainer className="input-container">
                <Input type="password" value={password} placeholder="비밀번호" autoComplete="off" onChange={onChangePassword} />
            </InputContainer>
            <ButtonContainer>
                <Button className="btn-hover" style={{backgroundColor: btnActivation?theme.btnColor:theme.unactivatedBtn}}>
                    Login
                </Button>
                <Button className="btn-hover" style={{backgroundColor: theme.btnColor}}>
                    <Link href="/signup"><a>Sign Up</a></Link>
                </Button>
            </ButtonContainer>
            <Partition>
                <HorizontalLine />
                <span style={{fontSize: '0.9rem', color: theme.fontGray}}>또는</span>
                <HorizontalLine />
            </Partition>
            <GuestLogin className="btn-hover" type="button">가계정으로 로그인하고 둘러보기</GuestLogin>
        </Form>
    );
};

export default LoginForm;
