import styled from 'styled-components';
import theme from '../../style/theme';
import { InputContainer, Input } from '../LoginForm/style';

export const Title = styled.h1`
    width:70%;
    margin-bottom:20px;
    font-family: 'GmarketSansMedium', '나눔고딕', NanumGothic, '돋움', Dotum, sans-serif;
    font-size:1.5rem;
    font-weight:bold
    color:#333;

    display:flex;
    justify-content:flex-begin;
`;

export const VerifyBtn = styled.button`
    width: 19%;
    color:#5DB09B;
    font-size:0.7rem;

    @media ${theme.mobileL} {
        font-size:0.6rem;
        width:23%
    }
`;

export const InputContainer2 = styled(InputContainer)`
    width:70%;

    @media ${theme.mobileL} {
        width:85%
    }

    @media ${theme.mobileS} {
        width:95%

`;

export const Input2 = styled(Input)`
    width:80%;

    @media ${theme.mobileL} {
        width:76%
    }    
`;

export const Indicator = styled.div`
    width: 70%;
    height:15px;
    padding: 0 4px;
    color:#5DB09B;
    font-size:0.7rem;

    display:flex;
    align-items:center;
`;
