import React, { useEffect, useState } from 'react';

import LoadingPage from '../LoadingPage';
import OrderResultCard from '../OrderResultCard';
import { Container, NameCard, FormContainer } from '../OrderForm/style';
import Axios from 'axios';

const OrderList = () => {
    const [loaded, setLoaded] = useState(false);
    const [orderInfo, setOrderInfo] = useState(null);
    const times = ['17:00', '18:00', '19:00', '20:00', '21:00']

    useEffect(async () => {
        await Axios.get('/reservation')
        .then(res => {
            console.log(res.data);
            setOrderInfo(res.data);
        })
        .then(() => {
            setLoaded(true)
        })
        .catch(err => console.error(err));
    }, []);

    return(
        <>
        {!loaded ? <LoadingPage /> :
        <Container style={{overflow:'auto', paddingTop:'2px'}}>
            {times.map(t => {
                return(
                    <>
                        <NameCard 
                            className="hover-shadow"
                            style={{
                                width:'99.5%', 
                                height:'50px', 
                                padding:'0 7px',
                                display:'flex',
                                alignItems:'center',
                                borderRadius:'4px',
                            }}>
                            {t}
                        </NameCard>
                        {orderInfo[t].length ? orderInfo[t].map(info => {
                            return <div style={{width:'98%'}}><OrderResultCard orderInfo={info} /></div>
                        }) : 
                        <NameCard style={{margin:'80px 0'}}>
                            예약 내역이 존재하지 않습니다.
                        </NameCard>
                        }
                        <div style={{
                            width:'100%',
                            height:'1px',
                            margin:'100px 0 50px',
                            borderBottom: '2px dashed #6c6a69'
                        }}> </div>
                    </>
                );
            })}
        </Container>}
        </>
    );
};

export default OrderList;
