import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';

import Header from '../components/Header';
import useWindowSize from '../hooks/useWindowSize';

const Main = styled.main`
    overflow-y:hidden;
    transition: all 0.5s ease-in-out;
`;

const FirstPage = styled.div`
    width:100%;

    background: no-repeat url("/img/main_back_img.jpg");
    background-size:100%;
    opacity: 0.7;

    @media (max-width:1305px) {
        background-size:cover;
        background-position:center;
    }
`;

const SecondPage = styled.div`
    width:100%;

`;

const Home = () => {
    const winSize = useWindowSize();
    const [curPage, setCurPage] = useState(0);
    const mainRef = useRef(null);

    useEffect(() => {
        mainRef.current.style.transform = `translateX(-${curPage}00%)`;
    }, [curPage]);

    return (
        <>
            <Header headerType={0} />
            <main ref={mainRef} style={{height:winSize[1]}}>
                <FirstPage style={{height:winSize[1]}}></FirstPage>
                <SecondPage style={{height:winSize[1]}}></SecondPage>
            </main>
        </>
    );
};

export default Home;
