import React from 'react';
import { useSelector } from 'react-redux';

import { Container, Title, Sub, BackBtn } from './style';
import DinnerSelectForm from './DinnerSelectForm';
import StyleSelectForm from './StyleSelectForm';
import OptionSelectForm from './OptionSelectForm';

const switchSubTitle = (step) => {
    switch(step) {
        case 1:
            return 'Dinner';
        case 2:
            return 'Style';
        case 3:
            return 'Options';
        case 4:
            return 'Order Informatoin';
        default:
            return '';
    }
};

const switchSelectForm = (step) => {
    switch(step) {
        case 1:
            return <DinnerSelectForm />;
        case 2:
            return <StyleSelectForm />;
        case 3:
            return <OptionSelectForm />;
        default:
            return '';
    }
};

const OrderForm = () => {
    const { step } = useSelector(state => state.order);

    return (
        <Container>
            <Title>
                주문하기
                <Sub>{switchSubTitle(step)}</Sub>
                <BackBtn>뒤로</BackBtn>
            </Title>
            {switchSelectForm(step)}
        </Container>
    );
};

export default OrderForm;
