import React, { useState, useEffect } from 'react';
import axios from 'axios';

import useWindowSize from '../../hooks/useWindowSize';
import { Main, Content, Title, Th, Td, SmallBtn, Input } from './style';
import LoadingPage from '../LoadingPage';
import useInput from '../../hooks/useInput';

const StockPage = () => {
    const winSize = useWindowSize();
    const [loaded, setLoaded] = useState(false);
    const [data, setData] = useState(null);
    const [tmpData, setTmpData] = useState(null);
    const [updateMode, setUpdateMode] = useState(false);

    useEffect(() => {
        const getStockInfo = async () => {
            await axios.get('/stock/read')
            .then(res => {
                console.log(res.data);
                setData(res.data);
                setLoaded(true);
            })
            .catch(err => {
                console.error(err);
            });
        };

        getStockInfo();
    }, []);

    const changeMode = () => {
        setUpdateMode(true);
        setTmpData(data);
    };

    const saveData = () => {

    };

    const cancelUpdate = () => {
        setData(tmpData);
        setUpdateMode(false);
    };

    return (
        <>
        {!loaded ?
        <LoadingPage /> :
        <Main style={{minHeight:winSize[1], overflow:'auto'}}>
            <Content>
                <div style={{display:'flex',alignItems:'flex-end', width:'100%', marginBottom:'30px'}}>
                    <Title>
                        재고 정보
                    </Title>
                    {updateMode
                    ? 
                    <>
                        <SmallBtn className="btn-hover" onClick={saveData}>
                            저장
                        </SmallBtn>
                        <SmallBtn className="btn-hover" onClick={cancelUpdate}>
                            취소
                        </SmallBtn>
                    </>
                    :
                    <SmallBtn className="btn-hover" onClick={changeMode}>
                        수정
                    </SmallBtn>}
                </div>
                <table style={{width:'100%'}}>
                    <thead>
                        <tr style={{display:'flex'}}>
                            <Th style={{width:'30px'}}>No</Th>
                            <Th style={{width:'calc((100%-110px)*2/5)'}}>메뉴 이름</Th>
                            <Th style={{width:'80px'}}>수량</Th>
                            <Th style={{width:'calc((100%-110px)*3/5)'}}>비고</Th>
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((v, i) => {
                            return(
                            <tr style={{display:'flex'}} key={v.menu_id}>
                                <Td style={{width:'30px'}}>{i+1}</Td>
                                <Td style={{width:'calc((100%-110px)*2/5)'}}>
                                    {updateMode
                                    ? <Input type="text" value={v.menu_name} autoComplete="off" />
                                    : v.menu_name}
                                </Td>
                                <Td style={{width:'80px'}}>{v.stock}</Td>
                                <Td style={{width:'calc((100%-110px)*3/5)'}}>{v.note}</Td>
                            </tr>
                            );
                        })}
                    </tbody>
                </table>
            </Content>
        </Main>}
        </>
    );
};

export default StockPage;
